let readline = require('readline')
'use strict'
let fs = require('fs'),
  path = require('path'),
  filePath = path.join(__dirname, 'SNP.txt')
let rs = []
/* pour l'instant je lis à un endorit fixe, faut adapter ca sur la récupération du fichier ds la page d'apres 
mais on le mettra surement sur la main page */
async function readFichier () {
  await fs.readFile(filePath, {
    encoding: 'utf-8'
  }, function (err, data) {
    if (!err) {
      console.log('received data: ' + data)
    } else {
      console.log(err)
    }
  })
}
async function writeFichier (infoSnp) {
  await fs.open('SNPInfo.txt', 'wx', function (err) {
    if (!err) {
      fs.writeFile('SNPInfo.txt', infoSnp, err => {
        if (err) throw err
      })
      console.log('file saved')
    } else {
      if (err.code === 'EEXIST') {
        console.log(err + ' : file already exist ')
        return
      }
    }
  })
}
module.exports.readFichier = readFichier
module.exports.writeFichier = writeFichier
